import React from "react";
import { Link, NavLink } from "react-router-dom";
import { sub_menu_buttons } from "../../constants/menu";
import { IconButton, InputBase } from "@mui/material";
import MenuIcon from "@mui/icons-material/Menu";
import SearchIcon from "@mui/icons-material/Search";
import Drawer from "@mui/material/Drawer";
import Box from "@mui/material/Box";

export default function ResponsiveDrawer() {
  const [state, setState] = React.useState({
    left: false,
  });
  const [searchTerm, setSearchTerm] = React.useState("");

  const toggleDrawer = (anchor, open) => (event) => {
    if (
      event.type === "keydown" &&
      (event.key === "Tab" || event.key === "Shift")
    ) {
      return;
    }

    setState({ ...state, [anchor]: open });
  };

  const list = (anchor) => (
    <Box
      sx={{
        width: anchor === "top" || anchor === "bottom" ? "auto" : 250,
        display: "flex",
        flexDirection: "column",
        justifyContent: "space-between",
        gap: "4",
      }}
      role="presentation"
      onClick={toggleDrawer(anchor, false)}
      onKeyDown={toggleDrawer(anchor, false)}
    >
      <InputBase
        placeholder="Search..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        sx={{
          border: "1px solid #ccc",
          borderRadius: "5px",
          padding: "8px",
        }}
      />
      <Link
        className="p-2 text-center hover:bg-orange-500 hover:text-white"
        to={"/"}
      >
        HOME
      </Link>
      ...
    </Box>
  );

  return (
    <div div className="sticky top-0 z-10">
      <div>
        <div>
          {["left"].map((anchor) => (
            <React.Fragment key={anchor}>
              <div className="p-4 bg-primary text-white ">
                <div className="flex justify-between  items-center">
                  <h2 className="text-2xl md:text-4xl font-bold duration-200 hover:scale-110 gradient_text">
                    <Link to={"/"} className="">
                      Sharanya Care
                    </Link>
                  </h2>
                  ...
                  <div className="flex lg:hidden gap-4 items-center">
                    <IconButton onClick={toggleDrawer(anchor, true)}>
                      <MenuIcon
                        fontSize="large"
                        sx={{
                          color: "white",
                        }}
                      />
                    </IconButton>
                  </div>
                </div>
                <Drawer
                  anchor={anchor}
                  open={state[anchor]}
                  onClose={toggleDrawer(anchor, false)}
                >
                  {list(anchor)}
                </Drawer>
              </div>
            </React.Fragment>
          ))}
        </div>
      </div>
      ...
    </div>
  );
}
