import React from "react";
import { Link } from "react-router-dom";

const WhyUs = () => {
  const WHY_US = [
    {
      title: "Sharanya Care is COVID-19 safe",
      description: "Your safety is taken care of by thermal screening, social distancing, sanitized clinics and hospital rooms, sterilized surgical equipment and mandatory PPE kits during surgery."
    },
    {
      title: "Social Distancing",
      description: "Maintaining a safe environment is crucial. Our facilities strictly adhere to social distancing guidelines to reduce the risk of viral transmission."
    },
    {
      title: "Sanitized Clinics and Hospital Rooms",
      description: "Our clinics and hospital rooms undergo rigorous sanitization procedures to ensure a clean and hygienic environment for your well-being."
    },
    {
      title: "Sterilized Surgical Equipment and Mandatory PPE Kits",
      description: "We prioritize infection control with sterilized surgical equipment and ensure the use of mandatory Personal Protective Equipment (PPE) kits during surgeries for the safety of both patients and healthcare providers."
    }
  ];

  const customColor = "#CDE0C9"; // Light green color
  const overlayOpacity = 0.7; // Opacity of the overlay (adjust as needed)

  return (
    <div className="grid grid-cols-1 md:grid-cols-3 gap-8 p-8">
      <div className="relative flex flex-col justify-center items-center gap-6 p-6" style={{ background: `linear-gradient(to bottom, ${customColor}, rgba(255, 255, 255, ${overlayOpacity}))`, color: "#000000", borderRadius: "15px", boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)" }}>
        <h4 className="text-2xl font-bold">Why Sharanya Care</h4>
        <p className="text-center">Delivering Seamless Surgical Experience in India</p>
        <Link to={'/book-appointment'} className="bg-white text-green-700 px-6 py-3 rounded-full shadow hover:bg-gray-100 transition duration-300">
          BOOK FREE APPOINTMENT
        </Link>
        {/* Lighter shade effect overlay */}
        <div className="absolute inset-0 bg-white opacity-25 rounded-lg"></div>
      </div>
      <div className="col-span-1 md:col-span-2 bg-gray-100 grid grid-cols-1 md:grid-cols-2 gap-8 p-8 rounded-lg shadow-lg">
        {WHY_US.map((item, i) => (
          <div key={i} className="flex flex-col gap-4 justify-center items-center p-6 bg-white shadow-md rounded-lg hover:shadow-xl transition duration-300">
            <div className="w-full flex flex-col gap-2 text-center">
              <h1 className="text-5xl font-bold text-green-700">0{i + 1}</h1>
              <h4 className="text-xl font-semibold text-gray-800">
                {item.title}
              </h4>
              <p className="text-gray-600">
                {item.description}
              </p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default WhyUs;
